import React from "react";
import "./Weather.css";
import Forecast from "./components/Forecast/Forecast";

class Weather extends React.Component {
  render() {
    const iconUrl = `http://openweathermap.org/img/wn/${this.state.icon}.png`;
    return (
      <div className="weather__details">
        {this.state.city && this.state.country && (
          <div>
            Location: {this.state.city}, {this.state.country}
          </div>
        )}
        {this.state.date && <div>Date: {this.state.date}</div>}
        {this.state.temperature && (
          <div>Temperature: {this.state.temperature}Â°C</div>
        )}
        {this.state.id && <div>ID: {this.state.id}</div>}
        {this.state.wind && <div>Wind: {this.state.wind}</div>}
        {this.state.description && <div>Weather: {this.state.description}</div>}
        {this.state.icon && <img src={iconUrl} alt={this.state.description}></img>}
        {this.state.error && <p>{this.state.error}</p>}
        <Forecast
          forecastOneDay={this.state.forecastOneDay}
          forecastTwoDay={this.state.forecastTwoDay}
          forecastThreeDay={this.state.forecastThreeDay}
          forecastFourDay={this.state.forecastFourDay}
          forecastFiveDay={this.state.forecastFiveDay}
          forecastIconOneDay={this.state.forecastIconOneDay}
          forecastIconTwoDay={this.state.forecastIconTwoDay}
          forecastIconThreeDay={this.state.forecastIconThreeDay}
          forecastIconFourDay={this.state.forecastIconFourDay}
          forecastIconFiveDay={this.state.forecastIconFiveDay}
        />
      </div>
    );
  }
}

export default Weather;
