import React from "react";
import Header from "../Header/Header";
import Form from "../Form/Form";
import Weather from "../Weather/Weather";
import "./App.css";

const API_KEY = "3fc44ed29eca0efe94b7e522e87c179e";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.props = {
      temperature: undefined,
      city: undefined,
      country: undefined,
      date: undefined,
      id: undefined,
      wind: undefined,
      description: undefined,
      icon: undefined,
      error: undefined,
      forecastOneDay: undefined,
      forecastTwoDay: undefined,
      forecastThreeDay: undefined,
      forecastFourDay: undefined,
      forecastFiveDay: undefined,
      forecastIconOneDay: undefined,
      forecastIconTwoDay: undefined,
      forecastIconThreeDay: undefined,
      forecastIconFourDay: undefined,
      forecastIconFiveDay: undefined,
    }
  }

  getWeather = async (event) => {
    event.preventDefault();
    const city = event.target.elements.city.value;
    const country = event.target.elements.country.value;
    const api_call = await fetch(`http://api.openweathermap.org/data/2.5/forecast?q=${city},${country}&appid=${API_KEY}&unit=metric`);
    const result = await api_call.json();
    if (city && country) {
      console.log(result);
      this.setState({
        temperature: Math.round(result.list[0].main.temp - 273.15),
        city: result.city.name,
        country: result.city.country,
        date: result.list[0].dt_txt.substring(0, 10),
        id: result.city.id,
        wind: result.list[0].wind.speed, 
        description: result.list[0].weather[0].description,
        icon: result.list[0].weather[0].icon,
        error: "",
        forecastOneDay: Math.round(result.list[4].main.temp - 273.15),
        forecastTwoDay: Math.round(result.list[12].main.temp - 273.15),
        forecastThreeDay: Math.round(result.list[20].main.temp - 273.15),
        forecastFourDay: Math.round(result.list[28].main.temp - 273.15),
        forecastFiveDay: Math.round(result.list[36].main.temp - 273.15),
      forecastIconOneDay: undefined,
      forecastIconOneDay: result.list[4].weather[0].icon,
        forecastIconTwoDay: result.list[12].weather[0].icon,
        forecastIconThreeDay: result.list[20].weather[0].icon,
        forecastIconFourDay: result.list[28].weather[0].icon,
        forecastIconFiveDay: result.list[36].weather[0].icon,
      })
    } else {
      this.setState({
        error: "Please enter a city and country"
      })
    }
  }

  render() {
    return (
      <div className="weather__container">
        <Header />
        <Form getWeather={ this.getWeather }/>
        <Weather 
          temperature={this.props.temperature}
          city={this.props.city}
          country={this.props.country}
          date={this.props.date}
          id={this.props.id}
          wind= {this.props.speed}
          description={this.props.description}
          icon={this.props.icon}
          error={this.props.error}
          forecastOneDay={this.props.forecastOneDay}
          forecastTwoDay={this.props.forecastTwoDay}
          forecastThreeDay={this.props.forecastThreeDay}
          forecastFourDay={this.props.forecastFourDay}
          forecastFiveDay={this.props.forecastFiveDay}
          forecastIconOneDay={this.props.forecastIconOneDay}
          forecastIconTwoDay={this.props.forecastIconTwoDay}
          forecastIconThreeDay={this.props.forecastIconThreeDay}
          forecastIconFourDay={this.props.forecastIconFourDay}
          forecastIconFiveDay={this.props.forecastIconFiveDay}
        />
      </div>
    );
  }
}

export default App;
