import React from "react";
import Titles from "../Titles/Titles";
import Form from "../Form/Form";
import Weather from "../Weather/Weather";
import "./App.css";

const API_KEY = "3fc44ed29eca0efe94b7e522e87c179e";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      temperature: undefined,
      city: undefined,
      country: undefined,
      humidity: undefined,
      wind: undefined,
      description: undefined,
      error: undefined,
    }
  }

  getWeather = async (event) => {
    event.preventDefault();
    const city = event.target.elements.city.value;
    const country = event.target.elements.country.value;
    const api_call = await fetch(`http://api.openweathermap.org/data/2.5/forecast?q=${city},${country}&appid=${API_KEY}&unit=metric`);
    const result = await api_call.json();
    if (city && country) {
      console.log(result);
      this.setState({
       /*  temperature: result.list[0].main.temp,
        city: result.city.name,
        country: result.city.country,
        humidity: result.list[0].weather[0].main,
        wind: result.result.list[0], 
        description: result.,
        error: "", */
      });
    } else {
      this.setState({
        error: "Please enter a city and country"
      })
    }
  }

  render() {
    return (

      <div className="weather__container">
        <Titles />
        <Form getWeather={ this.getWeather }/>
        <Weather 
          /* temperature={this.state.temperature}
          city={this.state.city}
          country={this.state.country}
          humidity={this.state.humidity}
          wind= {this.state.speed}
          description={this.state.description}
          error={this.state.error} */
        />
      </div>
    );
  }
}

export default App;
