import React from "react";
import "./Weather.css";

class Weather extends React.Component {
  render() {
    return (
      <div className="weather__details">
        { this.props.city && this.props.country && <div className="location">Location: { this.props.city }, { this.props.country}</div>}
        { this.props.temperature && <div className="temperature">Temperature: { this.props.temperature }</div> }
        { this.props.humidity && <div>Humidity: { this.props.humidity }</div> }
        { this.props.description && <div className="weather__description">Weather: { this.props.description }</div> }
        { this.props.error && <p>{ this.props.error }</p>}
      </div>
    )
 }  
}

export default Weather;
