import React from "react";
import "./Weather.css";
import Forecast from "./components/Forecast/Forecast";

class Weather extends React.Component {
  render() {
    const {
      city,
      country,
      date,
      temperature,
      id,
      wind,
      description,
      icon,
      error,
    } = this.props;
    const iconUrl = `http://openweathermap.org/img/wn/${icon}.png`;

    return (
      <div className="weather__details">
        {city && country && (
          <div>
            {" "}
            Location: {city}, {country}
          </div>
        )}
        {date && <div> Date: {date}</div>}
        {temperature && <div>Temperature: {temperature}Â°C</div>}
        {id && <div>ID: {id}</div>}
        {wind && <div>Wind: {wind}</div>}
        {description && <div>Weather: {description}</div>}
        {icon && (
          <img
            className="current-condition"
            src={iconUrl}
            alt={description}
          ></img>
        )}
        {error && <p>{error}</p>}
        <Forecast
          forecastOneDay={this.props.forecastOneDay}
          forecastTwoDay={this.props.forecastTwoDay}
          forecastThreeDay={this.props.forecastThreeDay}
          forecastFourDay={this.props.forecastFourDay}
          forecastFiveDay={this.props.forecastFiveDay}
          icon1={this.props.icon1}
          icon2={this.props.icon2}
          icon3={this.props.icon3}
          icon4={this.props.icon4}
          icon5={this.props.icon5}
        />
      </div>
    );
  }
}

export default Weather;
